mapper.select:
SELECT W.WEBTOON_CODE, W.WEBTOON_NAME, W.WEBTOON_LEVEL, W.WEBTOON_STATE, W.CATEGORY_CODE, W.PENALTY, W.NICKNAME,
W.FUNDING_PERIOD, W.SUMMARY, W.WEBTOON_THUMBNAIL, (SELECT SUM(HITS) FROM EPISODE WHERE WEBTOON_CODE = W.WEBTOON_CODE) AS HIT
FROM WEBTOON W
WHERE WEBTOON_LEVEL = #{webtoonLevel}

AND CATEGORY_CODE = #{categoryCode}

ORDER BY HIT

SELECT WEBTOON_NAME, WEBTOON_CODE, WEBTOON_STATE, CATEGORY_CODE, PENALTY, NICKNAME, FUNDING_PERIOD, SUMMARY
FROM WEBTOON
WHERE WEBTOON_NAME LIKE #{value} OR NICKNAME LIKE #{value}

SELECT NICKNAME
FROM WEBTOON
WHERE WEBTOON_CODE = #{value}

SELECT EPISODE_SEQUENCE, WEBTOON_CODE, EPISODE_NUMBER, HITS, RECOMMENDATION, UPLOAD_DATE, EPISODE_TITLE, AUTHOR_WORD, THUMBNAIL, FUND_CODE
FROM EPISODE
WHERE WEBTOON_CODE = #{value}

SELECT FILE_NAME
FROM IMAGE
WHERE EPISODE_SEQUENCE = #{value}

SELECT SELECT WEBTOON_CODE, WEBTOON_NAME, WEBTOON_LEVEL, WEBTOON_STATE, CATEGORY_CODE, PENALTY, NICKNAME, FUNDING_PERIOD, SUMMARY, WEBTOON_THUMBNAIL
FROM WEBTOON
WHERE WEBTOON_CODE = #{value}
